import React, { useState, useEffect } from 'react'
import Header from '../components/header'
import { key } from '../utils';
import MovieCart from '../components/movieCart'
import { Row, Col } from 'antd';

export default function Home() {
  const [data, setData] = useState(null)

  useEffect(() => {
    getList(key);
  }, [])

  async function getList(key) {
    try {
      let response = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${key}&language=en-US&page=32`);
      const data = await response.json();
      setData(data)
    } catch (err) {
      console.error(err);
      // Handle errors here
    }
  }
  console.log(data)
  return (
    <div>
      <Header />
      <div className="movie-section" >

        <Row gutter={[32, 32]} >
          {data && data.results.map(({ original_title, backdrop_path }) =>
            <Col span={4}>
              <div className="movie-cart" style={{ background: backdrop_path === null ? "#eee" : `url("https://image.tmdb.org/t/p/w342${backdrop_path}")` }} >
                <a href="">
                  <li>{original_title}</li>
                </a>
              </div>
            </Col>
          )}
        </Row>
      </div>
    </div>
  )
}
