import React, { useState } from 'react'
import search from '../img/search.png'
import { key } from '../utils';


export default function Search() {
  const [value, setValue] = useState("")
  const [data, setData] = useState(null)


  function handleChange(e) {
    setValue(e.target.value)
    search(key)
  }

  async function search(key) {
    try {
      let response = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${key}&language=en-US&query=${value}&page=1&include_adult=false`);
      const data = await response.json();
      setData(data)
    } catch (err) {
      console.error(err);
    }
  }

  console.log(value)
  console.log(data)
  return (
    <div className="search-bar">
      <div class="inner-search">
        <span ><img src={search} /></span>
        <input type="text" placeholder="Search" onChange={handleChange} />
      </div>
      <div class="search_result">
        {data.results && data.results.map(({ title, backdrop_path, }) =>
          <a href="">
            <div className="movie-cart" style={{ background: backdrop_path === null ? "#eee" : `url("https://image.tmdb.org/t/p/w342${backdrop_path}")` }} ></div>
            <div class="movie-caption">
              <h3 class="title">{title}</h3>
            </div>
          </a>
        )}
      </div>
    </div>
  )
}
