  const [data, setData] = useState(null)


  function handleChange(e) {
    setValue(e.target.value)
    search()
  }

  async function search(key) {
    try {
      let response = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=734c266a171652e992c46bc914cb2185&language=en-US&query=nb&page=1&include_adult=false`);
      const data = await response.json();
      setData(data)
    } catch (err) {
      console.error(err);
    }
  }

  console.log(value)
  console.log(data)
