import React, { useState, useEffect } from 'react'
import Header from '../components/header'
import Footer from '../components/footer'
import { key } from '../utils';
import { Row, Col } from 'antd';
import play from '../img/play-button.png';


export default function Home() {
  const [data, setData] = useState(null)

  useEffect(() => {
    getList(key);
  }, [])

  async function getList(key) {
    try {
      let response = await fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${key}&language=en-US&page=35`);
      const data = await response.json();
      setData(data)
    } catch (err) {
      console.error(err);
    }
  }

  handleClick = (e ,id) => {
    event
    alert(id)
  }

  return (
    <div>
      <Header />
      <div className="movie-section">
        <Row gutter={[32, 32]} >
          {data && data.results.map(({ title, backdrop_path, release_date, id }) =>
            <Col span={6}>
              <a href="" onClick={() => this.handleClick(e, id)} >
                <div className="movie-cart" style={{ background: backdrop_path === null ? "#eee" : `url("https://image.tmdb.org/t/p/w342${backdrop_path}")` }} ></div>
                <img src={play} />
                <div class="movie-caption">
                  <h3 class="title">{title}</h3>
                  <div class="movie-info">
                    <span class="movie-year">{release_date.substr(0, 4)}</span>
                    <span class="movie-ganre"></span>
                  </div>
                </div>
              </a>
            </Col>
          )}
        </Row>
      </div>
      <Footer/>
    </div >
  )
}
